(7, 6, 'App\\Models\\Clause', 1, '', 'clauses', 'CREATED', '{\"number\":\"1-Jan\",\"title\":\"Cybersecurity Strategy\",\"description\":\"To ensure that cybersecurity plans, goals, initiatives and projects are contributing to compliance with related laws and regulations.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":1}', '{\"new\": {\"id\": 1, \"title\": \"Cybersecurity Strategy\", \"number\": \"1-Jan\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"To ensure that cybersecurity plans, goals, initiatives and projects are contributing to compliance with related laws and regulations.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(8, 6, 'App\\Models\\Clause', 2, '', 'clauses', 'CREATED', '{\"number\":\"1\\/1\\/2001\",\"title\":\"Cybersecurity Strategy\",\"description\":\"A cybersecurity strategy must be defined, documented and approved. It must be supported by the head of the organization or his\\/her delegate (referred to in this document as Authorizing Official). The strategy goals must be in-line with related laws and regulations.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":2}', '{\"new\": {\"id\": 2, \"title\": \"Cybersecurity Strategy\", \"number\": \"1/1/2001\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"A cybersecurity strategy must be defined, documented and approved. It must be supported by the head of the organization or his/her delegate (referred to in this document as Authorizing Official). The strategy goals must be in-line with related laws and regulations.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(9, 6, 'App\\Models\\Clause', 3, '', 'clauses', 'CREATED', '{\"number\":\"1\\/1\\/2002\",\"title\":\"Cybersecurity Strategy\",\"description\":\"A roadmap must be executed to implement the cybersecurity strategy.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":3}', '{\"new\": {\"id\": 3, \"title\": \"Cybersecurity Strategy\", \"number\": \"1/1/2002\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"A roadmap must be executed to implement the cybersecurity strategy.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(10, 6, 'App\\Models\\Clause', 4, '', 'clauses', 'CREATED', '{\"number\":\"1\\/1\\/2003\",\"title\":\"Cybersecurity Strategy\",\"description\":\"The cybersecurity strategy must be reviewed periodically according to planned intervals or upon changes to related laws and regulations.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":4}', '{\"new\": {\"id\": 4, \"title\": \"Cybersecurity Strategy\", \"number\": \"1/1/2003\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"The cybersecurity strategy must be reviewed periodically according to planned intervals or upon changes to related laws and regulations.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(11, 6, 'App\\Models\\Clause', 5, '', 'clauses', 'CREATED', '{\"number\":\"2-Jan\",\"title\":\"Cybersecurity Management\",\"description\":\"To ensure Authorizing Official\\u0092s support in implementing and managing cybersecurity programs within the organization as per related laws and regulations\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":5}', '{\"new\": {\"id\": 5, \"title\": \"Cybersecurity Management\", \"number\": \"2-Jan\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"To ensure Authorizing Officials support in implementing and managing cybersecurity programs within the organization as per related laws and regulations\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(12, 6, 'App\\Models\\Clause', 6, '', 'clauses', 'CREATED', '{\"number\":\"1\\/2\\/2001\",\"title\":\"Cybersecurity Management\",\"description\":\"A dedicated cybersecurity function (e.g., division, department) must be established within the organization. This function must be independent from the Information Technology\\/Information Communication and Technology (IT\\/ICT) functions (as per the Royal Decree number 37140 dated 14\\/8\\/1438H). It is highly recommended that this cybersecurity function reports directly to the head of the organization or his\\/her delegate while ensuring that this does not result in a conflict of interest.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":6}', '{\"new\": {\"id\": 6, \"title\": \"Cybersecurity Management\", \"number\": \"1/2/2001\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"A dedicated cybersecurity function (e.g., division, department) must be established within the organization. This function must be independent from the Information Technology/Information Communication and Technology (IT/ICT) functions (as per the Royal Decree number 37140 dated 14/8/1438H). It is highly recommended that this cybersecurity function reports directly to the head of the organization or his/her delegate while ensuring that this does not result in a conflict of interest.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(13, 6, 'App\\Models\\Clause', 7, '', 'clauses', 'CREATED', '{\"number\":\"1\\/2\\/2002\",\"title\":\"Cybersecurity Management\",\"description\":\"The position of cybersecurity function head (e.g., CISO), and related supervisory and critical positions within the function, must be filled with full-time and experienced Saudi cybersecurity professionals.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":7}', '{\"new\": {\"id\": 7, \"title\": \"Cybersecurity Management\", \"number\": \"1/2/2002\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"The position of cybersecurity function head (e.g., CISO), and related supervisory and critical positions within the function, must be filled with full-time and experienced Saudi cybersecurity professionals.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(14, 6, 'App\\Models\\Clause', 8, '', 'clauses', 'CREATED', '{\"number\":\"1\\/2\\/2003\",\"title\":\"Cybersecurity Management\",\"description\":\"A cybersecurity steering committee must be established by the Authorizing Official to ensure the support and implementation of the cybersecurity programs and initiatives within the organization. Committee members, roles and responsibilities, and governance framework must be defined, documented and approved. The committee must include the head of the cybersecurity function as one of its members. It is highly recommended that the committee reports directly to the head of the organization or his\\/her delegate while ensuring that this does not result in a conflict of interest.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":8}', '{\"new\": {\"id\": 8, \"title\": \"Cybersecurity Management\", \"number\": \"1/2/2003\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"A cybersecurity steering committee must be established by the Authorizing Official to ensure the support and implementation of the cybersecurity programs and initiatives within the organization. Committee members, roles and responsibilities, and governance framework must be defined, documented and approved. The committee must include the head of the cybersecurity function as one of its members. It is highly recommended that the committee reports directly to the head of the organization or his/her delegate while ensuring that this does not result in a conflict of interest.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(15, 6, 'App\\Models\\Clause', 9, '', 'clauses', 'CREATED', '{\"number\":\"3-Jan\",\"title\":\"Cybersecurity Policies and Procedures\",\"description\":\"To ensure that cybersecurity requirements are documented, communicated and complied with by the organization as per related laws and regulations, and organizational requirements.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":9}', '{\"new\": {\"id\": 9, \"title\": \"Cybersecurity Policies and Procedures\", \"number\": \"3-Jan\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"To ensure that cybersecurity requirements are documented, communicated and complied with by the organization as per related laws and regulations, and organizational requirements.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(16, 6, 'App\\Models\\Clause', 10, '', 'clauses', 'CREATED', '{\"number\":\"1\\/3\\/2001\",\"title\":\"Cybersecurity Policies and Procedures\",\"description\":\"Cybersecurity policies and procedures must be defined and documented by the cybersecurity function, approved by the Authorizing Official, and disseminated to relevant parties inside and outside the organization.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":10}', '{\"new\": {\"id\": 10, \"title\": \"Cybersecurity Policies and Procedures\", \"number\": \"1/3/2001\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"Cybersecurity policies and procedures must be defined and documented by the cybersecurity function, approved by the Authorizing Official, and disseminated to relevant parties inside and outside the organization.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
(17, 6, 'App\\Models\\Clause', 11, '', 'clauses', 'CREATED', '{\"number\":\"1\\/3\\/2002\",\"title\":\"Cybersecurity Policies and Procedures\",\"description\":\"The cybersecurity function must ensure that the cybersecurity policies and procedures are implemented.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:14.000000Z\",\"created_at\":\"2021-11-26T10:44:14.000000Z\",\"id\":11}', '{\"new\": {\"id\": 11, \"title\": \"Cybersecurity Policies and Procedures\", \"number\": \"1/3/2002\", \"created_at\": \"2021-11-26 10:44:14\", \"updated_at\": \"2021-11-26 10:44:14\", \"description\": \"The cybersecurity function must ensure that the cybersecurity policies and procedures are implemented.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:14', '2021-11-26 10:44:14'),
