(45, 6, 'App\\Models\\Clause', 39, '', 'clauses', 'CREATED', '{\"number\":\"1\\/7\\/2001\",\"title\":\"Compliance with Cybersecurity Standards, Laws and Regulations\",\"description\":\"The organization must comply with related national cybersecurity laws and regulations.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":39}', '{\"new\": {\"id\": 39, \"title\": \"Compliance with Cybersecurity Standards, Laws and Regulations\", \"number\": \"1/7/2001\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"The organization must comply with related national cybersecurity laws and regulations.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
(46, 6, 'App\\Models\\Clause', 40, '', 'clauses', 'CREATED', '{\"number\":\"1\\/7\\/2002\",\"title\":\"Compliance with Cybersecurity Standards, Laws and Regulations\",\"description\":\"The organization must comply with any nationally-approved international agreements and commiments related to cybersecurity.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":40}', '{\"new\": {\"id\": 40, \"title\": \"Compliance with Cybersecurity Standards, Laws and Regulations\", \"number\": \"1/7/2002\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"The organization must comply with any nationally-approved international agreements and commiments related to cybersecurity.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
(47, 6, 'App\\Models\\Clause', 41, '', 'clauses', 'CREATED', '{\"number\":\"8-Jan\",\"title\":\"Periodical Cybersecurity Review and Audit\",\"description\":\"To ensure that cybersecurity controls are implemented and in compliance with organizational policies and procedures, as well as related national and international laws, regulations and agreements.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":41}', '{\"new\": {\"id\": 41, \"title\": \"Periodical Cybersecurity Review and Audit\", \"number\": \"8-Jan\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"To ensure that cybersecurity controls are implemented and in compliance with organizational policies and procedures, as well as related national and international laws, regulations and agreements.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
(48, 6, 'App\\Models\\Clause', 42, '', 'clauses', 'CREATED', '{\"number\":\"1\\/8\\/2001\",\"title\":\"Periodical Cybersecurity Review and Audit\",\"description\":\"Cybersecurity reviews must be conducted periodically by the cybersecurity function in the organization to assess the compliance with the cybersecurity controls in the organization.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":42}', '{\"new\": {\"id\": 42, \"title\": \"Periodical Cybersecurity Review and Audit\", \"number\": \"1/8/2001\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"Cybersecurity reviews must be conducted periodically by the cybersecurity function in the organization to assess the compliance with the cybersecurity controls in the organization.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
(49, 6, 'App\\Models\\Clause', 43, '', 'clauses', 'CREATED', '{\"number\":\"1\\/8\\/2002\",\"title\":\"Periodical Cybersecurity Review and Audit\",\"description\":\"Cybersecurity audits and reviews must be conducted by independent parties outside the cybersecurity function (e.g., Internal Audit function) to assess the compliance with the cybersecurity controls in the organization. Audits and reviews must be conducted independently, while ensuring that this does not result in a conflict of interest, as per the Generally Accepted Auditing Standards (GAAS), and related laws and regulations.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":43}', '{\"new\": {\"id\": 43, \"title\": \"Periodical Cybersecurity Review and Audit\", \"number\": \"1/8/2002\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"Cybersecurity audits and reviews must be conducted by independent parties outside the cybersecurity function (e.g., Internal Audit function) to assess the compliance with the cybersecurity controls in the organization. Audits and reviews must be conducted independently, while ensuring that this does not result in a conflict of interest, as per the Generally Accepted Auditing Standards (GAAS), and related laws and regulations.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
(50, 6, 'App\\Models\\Clause', 44, '', 'clauses', 'CREATED', '{\"number\":\"1\\/8\\/2003\",\"title\":\"Periodical Cybersecurity Review and Audit\",\"description\":\"Results from the cybersecurity audits and reviews must be documented and presented to the cybersecurity steering committee and Authorizing Official. Results must include the audit\\/review scope, observations, recommendations and remediation plans.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":44}', '{\"new\": {\"id\": 44, \"title\": \"Periodical Cybersecurity Review and Audit\", \"number\": \"1/8/2003\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"Results from the cybersecurity audits and reviews must be documented and presented to the cybersecurity steering committee and Authorizing Official. Results must include the audit/review scope, observations, recommendations and remediation plans.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
(51, 6, 'App\\Models\\Clause', 45, '', 'clauses', 'CREATED', '{\"number\":\"9-Jan\",\"title\":\"Cybersecurity in Human Resources\",\"description\":\"To ensure that cybersecurity risks and requirements related to personnel (employees and contractors) are managed efficiently prior to employment, during employment and after termination\\/separation as per organizational policies and procedures, and related laws and regulations.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":45}', '{\"new\": {\"id\": 45, \"title\": \"Cybersecurity in Human Resources\", \"number\": \"9-Jan\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"To ensure that cybersecurity risks and requirements related to personnel (employees and contractors) are managed efficiently prior to employment, during employment and after termination/separation as per organizational policies and procedures, and related laws and regulations.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
(52, 6, 'App\\Models\\Clause', 46, '', 'clauses', 'CREATED', '{\"number\":\"1\\/9\\/2001\",\"title\":\"Cybersecurity in Human Resources\",\"description\":\"Personnel cybersecurity requirements (prior to employment, during employment and after termination\\/separation) must be defined, documented and approved.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":46}', '{\"new\": {\"id\": 46, \"title\": \"Cybersecurity in Human Resources\", \"number\": \"1/9/2001\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"Personnel cybersecurity requirements (prior to employment, during employment and after termination/separation) must be defined, documented and approved.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
