(62, 6, 'App\\Models\\Clause', 56, '', 'clauses', 'CREATED', '{\"number\":\"10-Jan\",\"title\":\"Cybersecurity Awareness and Training Program\",\"description\":\"To ensure that personnel are aware of their cybersecurity responsibilities and have the essential cybersecurity awareness. It is also to ensure that personnel are provided with the required cybersecurity training, skills and credentials needed to accomplish their cybersecurity responsibilities and to protect the organization\\u0092s information and technology assets.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":56}', '{\"new\": {\"id\": 56, \"title\": \"Cybersecurity Awareness and Training Program\", \"number\": \"10-Jan\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"To ensure that personnel are aware of their cybersecurity responsibilities and have the essential cybersecurity awareness. It is also to ensure that personnel are provided with the required cybersecurity training, skills and credentials needed to accomplish their cybersecurity responsibilities and to protect the organizations information and technology assets.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
(63, 6, 'App\\Models\\Clause', 57, '', 'clauses', 'CREATED', '{\"number\":\"1\\/10\\/2001\",\"title\":\"Cybersecurity Awareness and Training Program\",\"description\":\"A cybersecurity awareness program must be developed and approved. The program must be conducted periodically through multiple channels to strengthen the awareness about cybersecurity, cyber threats and risks, and to build a positive cybersecurity awareness culture.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":57}', '{\"new\": {\"id\": 57, \"title\": \"Cybersecurity Awareness and Training Program\", \"number\": \"1/10/2001\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"A cybersecurity awareness program must be developed and approved. The program must be conducted periodically through multiple channels to strengthen the awareness about cybersecurity, cyber threats and risks, and to build a positive cybersecurity awareness culture.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
(64, 6, 'App\\Models\\Clause', 58, '', 'clauses', 'CREATED', '{\"number\":\"1\\/10\\/2002\",\"title\":\"Cybersecurity Awareness and Training Program\",\"description\":\"The cybersecurity awareness program must be implemented.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T10:44:15.000000Z\",\"created_at\":\"2021-11-26T10:44:15.000000Z\",\"id\":58}', '{\"new\": {\"id\": 58, \"title\": \"Cybersecurity Awareness and Training Program\", \"number\": \"1/10/2002\", \"created_at\": \"2021-11-26 10:44:15\", \"updated_at\": \"2021-11-26 10:44:15\", \"description\": \"The cybersecurity awareness program must be implemented.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:44:15', '2021-11-26 10:44:15'),
