(317, 6, 'App\\Models\\Clause', 311, '', 'clauses', 'CREATED', '{\"number\":\"2-6\",\"title\":\"Data and Information Protection\",\"description\":\"To ensure the confidentiality, integrity and availability of the organization\'s data and information as per organizational policies and procedures, and related laws and regulations.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":311}', '{\"new\": {\"id\": 311, \"title\": \"Data and Information Protection\", \"number\": \"2-6\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"To ensure the confidentiality, integrity and availability of the organization\'s data and information as per organizational policies and procedures, and related laws and regulations.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(318, 6, 'App\\Models\\Clause', 312, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1\",\"title\":\"Data and Information Protection\",\"description\":\"In additional to the subcontrols in ECC control 2-7-3, cybersecurity requirements for protecting and handling data and information must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":312}', '{\"new\": {\"id\": 312, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"In additional to the subcontrols in ECC control 2-7-3, cybersecurity requirements for protecting and handling data and information must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(319, 6, 'App\\Models\\Clause', 313, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-1\",\"title\":\"Data and Information Protection\",\"description\":\"Prohibiting the use of critical system\'s data in any environment other than production environment, except after applying strict controls for protecting that data, such as: data masking or data scrambling techniques.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":313}', '{\"new\": {\"id\": 313, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-1\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Prohibiting the use of critical system\'s data in any environment other than production environment, except after applying strict controls for protecting that data, such as: data masking or data scrambling techniques.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(320, 6, 'App\\Models\\Clause', 314, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-2\",\"title\":\"Data and Information Protection\",\"description\":\"Classifying all data within critical systems.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":314}', '{\"new\": {\"id\": 314, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-2\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Classifying all data within critical systems.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(321, 6, 'App\\Models\\Clause', 315, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-3\",\"title\":\"Data and Information Protection\",\"description\":\"Protecting classified data of critical systems using data leakage prevention techniques.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":315}', '{\"new\": {\"id\": 315, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-3\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Protecting classified data of critical systems using data leakage prevention techniques.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(322, 6, 'App\\Models\\Clause', 316, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-4\",\"title\":\"Data and Information Protection\",\"description\":\"Identifying retention period for critical systems-associated data, in accordance with relevant legislations. Only required data must be retained in critical systems\' production environments.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":316}', '{\"new\": {\"id\": 316, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-4\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Identifying retention period for critical systems-associated data, in accordance with relevant legislations. Only required data must be retained in critical systems\' production environments.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(323, 6, 'App\\Models\\Clause', 317, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-5\",\"title\":\"Data and Information Protection\",\"description\":\"Prohibiting the transfer of any critical systems\' data from production environment to any other environment.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":317}', '{\"new\": {\"id\": 317, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-5\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Prohibiting the transfer of any critical systems\' data from production environment to any other environment.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(324, 6, 'App\\Models\\Clause', 318, '', 'clauses', 'CREATED', '{\"number\":\"2-7\",\"title\":\"Cryptography\",\"description\":\"To ensure the proper and efficient use of cryptograpy to protect information assets as per organizational policies and procedures, and related laws and regulations.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":318}', '{\"new\": {\"id\": 318, \"title\": \"Cryptography\", \"number\": \"2-7\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"To ensure the proper and efficient use of cryptograpy to protect information assets as per organizational policies and procedures, and related laws and regulations.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(325, 6, 'App\\Models\\Clause', 319, '', 'clauses', 'CREATED', '{\"number\":\"2-7-1\",\"title\":\"Cryptography\",\"description\":\"In additional to the subcontrols in ECC control 2-8-3, cybersecurity requirements for cryptography must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":319}', '{\"new\": {\"id\": 319, \"title\": \"Cryptography\", \"number\": \"2-7-1\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"In additional to the subcontrols in ECC control 2-8-3, cybersecurity requirements for cryptography must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(326, 6, 'App\\Models\\Clause', 320, '', 'clauses', 'CREATED', '{\"number\":\"2-7-1-1\",\"title\":\"Cryptography\",\"description\":\"Encrypting all critical systems\' data-in-transit.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":320}', '{\"new\": {\"id\": 320, \"title\": \"Cryptography\", \"number\": \"2-7-1-1\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Encrypting all critical systems\' data-in-transit.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(327, 6, 'App\\Models\\Clause', 321, '', 'clauses', 'CREATED', '{\"number\":\"2-7-1-2\",\"title\":\"Cryptography\",\"description\":\"Encrypting all critical systems\' data-at-rest at the level fo files, database or certain columns within database.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":321}', '{\"new\": {\"id\": 321, \"title\": \"Cryptography\", \"number\": \"2-7-1-2\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Encrypting all critical systems\' data-at-rest at the level fo files, database or certain columns within database.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(328, 6, 'App\\Models\\Clause', 322, '', 'clauses', 'CREATED', '{\"number\":\"2-7-1-3\",\"title\":\"Cryptography\",\"description\":\"Using secure and up-to-date methods, algorithms, keys and devices in accordance with what NCA issues in this regard.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":322}', '{\"new\": {\"id\": 322, \"title\": \"Cryptography\", \"number\": \"2-7-1-3\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Using secure and up-to-date methods, algorithms, keys and devices in accordance with what NCA issues in this regard.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(329, 6, 'App\\Models\\Clause', 323, '', 'clauses', 'CREATED', '{\"number\":\"2-8\",\"title\":\"Backup and Recovery Management\",\"description\":\"To ensure the protection of the organization\'s data and information, including information systems\' and software configuration from cyber risks as per organizational policies and procedures, and related laws and regulations.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":323}', '{\"new\": {\"id\": 323, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"To ensure the protection of the organization\'s data and information, including information systems\' and software configuration from cyber risks as per organizational policies and procedures, and related laws and regulations.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(330, 6, 'App\\Models\\Clause', 324, '', 'clauses', 'CREATED', '{\"number\":\"2-8-1\",\"title\":\"Backup and Recovery Management\",\"description\":\"In addition to the subcontrols in ECC control 2-9-3, cybersecurity requirements for backup and recovery management must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":324}', '{\"new\": {\"id\": 324, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8-1\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"In addition to the subcontrols in ECC control 2-9-3, cybersecurity requirements for backup and recovery management must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(331, 6, 'App\\Models\\Clause', 325, '', 'clauses', 'CREATED', '{\"number\":\"2-8-1-1\",\"title\":\"Backup and Recovery Management\",\"description\":\"Scope and coverage of online and offline backups shall cover all critical systems.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":325}', '{\"new\": {\"id\": 325, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8-1-1\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Scope and coverage of online and offline backups shall cover all critical systems.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(332, 6, 'App\\Models\\Clause', 326, '', 'clauses', 'CREATED', '{\"number\":\"2-8-1-2\",\"title\":\"Backup and Recovery Management\",\"description\":\"Performing backup within planned intervals, according to the organization\'s risk assessment. NCA recommends performing bacup for critical systems on a daily basis.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":326}', '{\"new\": {\"id\": 326, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8-1-2\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Performing backup within planned intervals, according to the organization\'s risk assessment. NCA recommends performing bacup for critical systems on a daily basis.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(333, 6, 'App\\Models\\Clause', 327, '', 'clauses', 'CREATED', '{\"number\":\"2-8-1-3\",\"title\":\"Backup and Recovery Management\",\"description\":\"Securing access, storage and transfer of critical systems\' backups and storage media, and protecting it from destruction, unauthorized access or modification.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":327}', '{\"new\": {\"id\": 327, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8-1-3\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Securing access, storage and transfer of critical systems\' backups and storage media, and protecting it from destruction, unauthorized access or modification.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(334, 6, 'App\\Models\\Clause', 328, '', 'clauses', 'CREATED', '{\"number\":\"2-8-2\",\"title\":\"Backup and Recovery Management\",\"description\":\"With reference to ECC subcontrol 2-9-3-3, a periodical test must be conducted at least once every three months in order to determine the efficiency of recovering critical systems backups.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":328}', '{\"new\": {\"id\": 328, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8-2\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"With reference to ECC subcontrol 2-9-3-3, a periodical test must be conducted at least once every three months in order to determine the efficiency of recovering critical systems backups.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(335, 6, 'App\\Models\\Clause', 329, '', 'clauses', 'CREATED', '{\"number\":\"2-9\",\"title\":\"Vulnerabilities Management\",\"description\":\"To ensure timely detection and effective remediation of technical vulnerabilities to prevent or minimize the probability of exploiting these vulnerabilities to launch cyber attacks against the organization.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":329}', '{\"new\": {\"id\": 329, \"title\": \"Vulnerabilities Management\", \"number\": \"2-9\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"To ensure timely detection and effective remediation of technical vulnerabilities to prevent or minimize the probability of exploiting these vulnerabilities to launch cyber attacks against the organization.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(336, 6, 'App\\Models\\Clause', 330, '', 'clauses', 'CREATED', '{\"number\":\"2-9-1\",\"title\":\"Vulnerabilities Management\",\"description\":\"In addition to the the subcontrols in ECC control 2-10-3, cybersecurity requirements for technical vulnerabilities management of critical systems must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":330}', '{\"new\": {\"id\": 330, \"title\": \"Vulnerabilities Management\", \"number\": \"2-9-1\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"In addition to the the subcontrols in ECC control 2-10-3, cybersecurity requirements for technical vulnerabilities management of critical systems must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(337, 6, 'App\\Models\\Clause', 331, '', 'clauses', 'CREATED', '{\"number\":\"2-9-1-1\",\"title\":\"Vulnerabilities Management\",\"description\":\"Utilizing trusted methods and tools for vulnerabilities assessments.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":331}', '{\"new\": {\"id\": 331, \"title\": \"Vulnerabilities Management\", \"number\": \"2-9-1-1\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Utilizing trusted methods and tools for vulnerabilities assessments.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(338, 6, 'App\\Models\\Clause', 332, '', 'clauses', 'CREATED', '{\"number\":\"2-9-1-2\",\"title\":\"Vulnerabilities Management\",\"description\":\"Assessing and remediating vulnerabilities (by installing security policies and patches) on technical components of critical systems at least once every month for external and internet-connected critical systems, and at least once every three months for internal critical systems.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":332}', '{\"new\": {\"id\": 332, \"title\": \"Vulnerabilities Management\", \"number\": \"2-9-1-2\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Assessing and remediating vulnerabilities (by installing security policies and patches) on technical components of critical systems at least once every month for external and internet-connected critical systems, and at least once every three months for internal critical systems.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(339, 6, 'App\\Models\\Clause', 333, '', 'clauses', 'CREATED', '{\"number\":\"2-9-1-3\",\"title\":\"Vulnerabilities Management\",\"description\":\"Immediately remediating for critical vulnerabilities, in line with change management processes approved by the organization.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":333}', '{\"new\": {\"id\": 333, \"title\": \"Vulnerabilities Management\", \"number\": \"2-9-1-3\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"Immediately remediating for critical vulnerabilities, in line with change management processes approved by the organization.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
(340, 6, 'App\\Models\\Clause', 334, '', 'clauses', 'CREATED', '{\"number\":\"2-9-2\",\"title\":\"Vulnerabilities Management\",\"description\":\"With reference to ECC subcontrol 2-10-3-1, vulnerabilities assessments must be conducted on critical systems\' technical components at least once every month.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:48.000000Z\",\"created_at\":\"2021-11-26T10:45:48.000000Z\",\"id\":334}', '{\"new\": {\"id\": 334, \"title\": \"Vulnerabilities Management\", \"number\": \"2-9-2\", \"created_at\": \"2021-11-26 10:45:48\", \"updated_at\": \"2021-11-26 10:45:48\", \"description\": \"With reference to ECC subcontrol 2-10-3-1, vulnerabilities assessments must be conducted on critical systems\' technical components at least once every month.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:48', '2021-11-26 10:45:48'),
