(376, 6, 'App\\Models\\Clause', 370, '', 'clauses', 'CREATED', '{\"number\":\"4-1\",\"title\":\"Third Party and Cloud Computing Cybersecurity\",\"description\":\"To ensure the protection of assets against cybersecurity risks related to third parties, including outsourcing and managed services as per organizational policies and procedures, related laws and regulations.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:49.000000Z\",\"created_at\":\"2021-11-26T10:45:49.000000Z\",\"id\":370}', '{\"new\": {\"id\": 370, \"title\": \"Third Party and Cloud Computing Cybersecurity\", \"number\": \"4-1\", \"created_at\": \"2021-11-26 10:45:49\", \"updated_at\": \"2021-11-26 10:45:49\", \"description\": \"To ensure the protection of assets against cybersecurity risks related to third parties, including outsourcing and managed services as per organizational policies and procedures, related laws and regulations.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:49', '2021-11-26 10:45:49'),
(377, 6, 'App\\Models\\Clause', 371, '', 'clauses', 'CREATED', '{\"number\":\"4-1-1\",\"title\":\"Third-Party Cybersecurity\",\"description\":\"In addition to the controls in ECC subdomain 4-1, cybersecurity requriements for contracts and agreements with third-parties must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:49.000000Z\",\"created_at\":\"2021-11-26T10:45:49.000000Z\",\"id\":371}', '{\"new\": {\"id\": 371, \"title\": \"Third-Party Cybersecurity\", \"number\": \"4-1-1\", \"created_at\": \"2021-11-26 10:45:49\", \"updated_at\": \"2021-11-26 10:45:49\", \"description\": \"In addition to the controls in ECC subdomain 4-1, cybersecurity requriements for contracts and agreements with third-parties must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:49', '2021-11-26 10:45:49'),
(378, 6, 'App\\Models\\Clause', 372, '', 'clauses', 'CREATED', '{\"number\":\"4-1-1-1\",\"title\":\"Third-Party Cybersecurity\",\"description\":\"Screening or vetting of outsourcing and managed services companies and personnel who work on critical systems.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:49.000000Z\",\"created_at\":\"2021-11-26T10:45:49.000000Z\",\"id\":372}', '{\"new\": {\"id\": 372, \"title\": \"Third-Party Cybersecurity\", \"number\": \"4-1-1-1\", \"created_at\": \"2021-11-26 10:45:49\", \"updated_at\": \"2021-11-26 10:45:49\", \"description\": \"Screening or vetting of outsourcing and managed services companies and personnel who work on critical systems.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:49', '2021-11-26 10:45:49'),
(379, 6, 'App\\Models\\Clause', 373, '', 'clauses', 'CREATED', '{\"number\":\"4-1-1-2\",\"title\":\"Third-Party Cybersecurity\",\"description\":\"Outsourcing and managed services of critical systems must rely on Saudi companies and organizations, in accordance with the relevant legislative and regulatory requirements.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:49.000000Z\",\"created_at\":\"2021-11-26T10:45:49.000000Z\",\"id\":373}', '{\"new\": {\"id\": 373, \"title\": \"Third-Party Cybersecurity\", \"number\": \"4-1-1-2\", \"created_at\": \"2021-11-26 10:45:49\", \"updated_at\": \"2021-11-26 10:45:49\", \"description\": \"Outsourcing and managed services of critical systems must rely on Saudi companies and organizations, in accordance with the relevant legislative and regulatory requirements.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:49', '2021-11-26 10:45:49'),
(380, 6, 'App\\Models\\Clause', 374, '', 'clauses', 'CREATED', '{\"number\":\"4-2\",\"title\":\"Cloud Computing and Hosting Cybersecurity\",\"description\":\"To ensure the proper and efficient remediation of cyber risks and the implementation of cybersecurity requirements related to hosting and cloud computing as per organizational prolicies and procedures, and related laws and regulations. It is also to ensure the protection of the organization\'s information technology assets hosted on the cloud or processed\\/managed by third parties.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:49.000000Z\",\"created_at\":\"2021-11-26T10:45:49.000000Z\",\"id\":374}', '{\"new\": {\"id\": 374, \"title\": \"Cloud Computing and Hosting Cybersecurity\", \"number\": \"4-2\", \"created_at\": \"2021-11-26 10:45:49\", \"updated_at\": \"2021-11-26 10:45:49\", \"description\": \"To ensure the proper and efficient remediation of cyber risks and the implementation of cybersecurity requirements related to hosting and cloud computing as per organizational prolicies and procedures, and related laws and regulations. It is also to ensure the protection of the organization\'s information technology assets hosted on the cloud or processed/managed by third parties.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:49', '2021-11-26 10:45:49'),
(381, 6, 'App\\Models\\Clause', 375, '', 'clauses', 'CREATED', '{\"number\":\"4-2-1\",\"title\":\"Cloud Computing and Hosting Cybersecurity\",\"description\":\"In addition to the subcontrols in ECC control 4-2-3, cybersecurity requirements related to the use of the hosting and cloud computing services must include must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:49.000000Z\",\"created_at\":\"2021-11-26T10:45:49.000000Z\",\"id\":375}', '{\"new\": {\"id\": 375, \"title\": \"Cloud Computing and Hosting Cybersecurity\", \"number\": \"4-2-1\", \"created_at\": \"2021-11-26 10:45:49\", \"updated_at\": \"2021-11-26 10:45:49\", \"description\": \"In addition to the subcontrols in ECC control 4-2-3, cybersecurity requirements related to the use of the hosting and cloud computing services must include must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:49', '2021-11-26 10:45:49'),
(382, 6, 'App\\Models\\Clause', 376, '', 'clauses', 'CREATED', '{\"number\":\"4-2-1-1\",\"title\":\"Cloud Computing and Hosting Cybersecurity\",\"description\":\"Hosting of critical systems and any part of their technical components must be inside the organization or within cloud computing services provided by government organizations or Saudi companies that are in compliance with NCA\'s Cloud Cybersecurity Controls (CCC), taking into account the classification of the hosted data.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T10:45:49.000000Z\",\"created_at\":\"2021-11-26T10:45:49.000000Z\",\"id\":376}', '{\"new\": {\"id\": 376, \"title\": \"Cloud Computing and Hosting Cybersecurity\", \"number\": \"4-2-1-1\", \"created_at\": \"2021-11-26 10:45:49\", \"updated_at\": \"2021-11-26 10:45:49\", \"description\": \"Hosting of critical systems and any part of their technical components must be inside the organization or within cloud computing services provided by government organizations or Saudi companies that are in compliance with NCA\'s Cloud Cybersecurity Controls (CCC), taking into account the classification of the hosted data.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:49', '2021-11-26 10:45:49'),
(383, 6, 'App\\Models\\Clause', 377, '', 'clauses', 'CREATED', '{\"number\":\"5\",\"title\":\"General Requirements\",\"description\":\"The following requirements contain the mandatory procedure and technical methods the FO shall implement in order to protect the cybersecurity of security systems and IA&CS Assets.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":377}', '{\"new\": {\"id\": 377, \"title\": \"General Requirements\", \"number\": \"5\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The following requirements contain the mandatory procedure and technical methods the FO shall implement in order to protect the cybersecurity of security systems and IA&CS Assets.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(384, 6, 'App\\Models\\Clause', 378, '', 'clauses', 'CREATED', '{\"number\":\"5.1-Para 1\",\"title\":\"Cybersecurity Management\",\"description\":\"FO shall set clear and measurable policy and procedures to protect security systems and IA&CS Assets through the implementation of Cybersecurity Management (CSM). The CSM shall be designed to protect security systems & IA&CS assets from all threats, whether internal or external, deliberate or accidental.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":378}', '{\"new\": {\"id\": 378, \"title\": \"Cybersecurity Management\", \"number\": \"5.1-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"FO shall set clear and measurable policy and procedures to protect security systems and IA&CS Assets through the implementation of Cybersecurity Management (CSM). The CSM shall be designed to protect security systems & IA&CS assets from all threats, whether internal or external, deliberate or accidental.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(385, 6, 'App\\Models\\Clause', 379, '', 'clauses', 'CREATED', '{\"number\":\"5.1-Para 2\",\"title\":\"Cybersecurity Management\",\"description\":\"The CSM shall be reviewed, and updated where needed every 12 calendar months or immediately following recovery from cybersecurity incident. Changes to the CSM shall be reviewed by facility executive management who shall certify its compliance with facility cybersecurity requirements.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":379}', '{\"new\": {\"id\": 379, \"title\": \"Cybersecurity Management\", \"number\": \"5.1-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The CSM shall be reviewed, and updated where needed every 12 calendar months or immediately following recovery from cybersecurity incident. Changes to the CSM shall be reviewed by facility executive management who shall certify its compliance with facility cybersecurity requirements.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(386, 6, 'App\\Models\\Clause', 380, '', 'clauses', 'CREATED', '{\"number\":\"5.1-Para 3\",\"title\":\"Cybersecurity Management\",\"description\":\"The CSM shall formally assign accountability and responsibility for cybersecurity to a designated organization.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":380}', '{\"new\": {\"id\": 380, \"title\": \"Cybersecurity Management\", \"number\": \"5.1-Para 3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The CSM shall formally assign accountability and responsibility for cybersecurity to a designated organization.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(387, 6, 'App\\Models\\Clause', 381, '', 'clauses', 'CREATED', '{\"number\":\"5.1-Para 4\",\"title\":\"Cybersecurity Management\",\"description\":\"CSM shall cover all items in this document. Changes to the CSM shall be summarized and sent to HCIS.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":381}', '{\"new\": {\"id\": 381, \"title\": \"Cybersecurity Management\", \"number\": \"5.1-Para 4\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"CSM shall cover all items in this document. Changes to the CSM shall be summarized and sent to HCIS.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
