(389, 6, 'App\\Models\\Clause', 383, '', 'clauses', 'CREATED', '{\"number\":\"5.2.1\",\"title\":\"Risk Assessment & Management\",\"description\":\"The FO shall conduct and document a cyber-risk assessment that includes identification of critical IA&CS assets.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":383}', '{\"new\": {\"id\": 383, \"title\": \"Risk Assessment & Management\", \"number\": \"5.2.1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall conduct and document a cyber-risk assessment that includes identification of critical IA&CS assets.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(390, 6, 'App\\Models\\Clause', 384, '', 'clauses', 'CREATED', '{\"number\":\"5.2.2\",\"title\":\"Risk Assessment & Management\",\"description\":\"The FO shall address all identified risks with the appropriate treatment Penetration testing shall be utilized as part of risk identification process.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":384}', '{\"new\": {\"id\": 384, \"title\": \"Risk Assessment & Management\", \"number\": \"5.2.2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall address all identified risks with the appropriate treatment Penetration testing shall be utilized as part of risk identification process.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(391, 6, 'App\\Models\\Clause', 385, '', 'clauses', 'CREATED', '{\"number\":\"5.2.3\",\"title\":\"Risk Assessment & Management\",\"description\":\"The cyber-risk assessment shall be conducted utilizing a prevailing industry methodology such as, but not limited to, the following:\\nFO shall identify the methodology used for the assessment.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":385}', '{\"new\": {\"id\": 385, \"title\": \"Risk Assessment & Management\", \"number\": \"5.2.3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The cyber-risk assessment shall be conducted utilizing a prevailing industry methodology such as, but not limited to, the following:\\nFO shall identify the methodology used for the assessment.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
