(394, 6, 'App\\Models\\Clause', 388, '', 'clauses', 'CREATED', '{\"number\":\"5.2.3.3\",\"title\":\"Risk Assessment & Management\",\"description\":\"ISA 62443\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":388}', '{\"new\": {\"id\": 388, \"title\": \"Risk Assessment & Management\", \"number\": \"5.2.3.3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"ISA 62443\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(395, 6, 'App\\Models\\Clause', 389, '', 'clauses', 'CREATED', '{\"number\":\"5.2.4\",\"title\":\"Risk Assessment & Management\",\"description\":\"A cyber-risk assessment shall be conducted either when a major change occurs to facility IA&CS or security systems or every 12 months.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":389}', '{\"new\": {\"id\": 389, \"title\": \"Risk Assessment & Management\", \"number\": \"5.2.4\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"A cyber-risk assessment shall be conducted either when a major change occurs to facility IA&CS or security systems or every 12 months.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(396, 6, 'App\\Models\\Clause', 390, '', 'clauses', 'CREATED', '{\"number\":\"5.2.5\",\"title\":\"Risk Assessment & Management\",\"description\":\"The results of the cyber-risk assessment shall be summarized and sent to HCIS whenever the assessment is changed and shall be validated by an external audit conducted every 12 months.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":390}', '{\"new\": {\"id\": 390, \"title\": \"Risk Assessment & Management\", \"number\": \"5.2.5\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The results of the cyber-risk assessment shall be summarized and sent to HCIS whenever the assessment is changed and shall be validated by an external audit conducted every 12 months.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(397, 6, 'App\\Models\\Clause', 391, '', 'clauses', 'CREATED', '{\"number\":\"5.3-Para 1\",\"title\":\"Cyber Seurity Training & Awareness\",\"description\":\"The FO shall provide cyber security training & awareness for personnel with access to the FO\\u0092s security systems & IA&CS assets. Training shall be refreshed periodically and shall not exceed 24 months between refreshers.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":391}', '{\"new\": {\"id\": 391, \"title\": \"Cyber Seurity Training & Awareness\", \"number\": \"5.3-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall provide cyber security training & awareness for personnel with access to the FOs security systems & IA&CS assets. Training shall be refreshed periodically and shall not exceed 24 months between refreshers.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(398, 6, 'App\\Models\\Clause', 392, '', 'clauses', 'CREATED', '{\"number\":\"5.3-Para 2\",\"title\":\"Cyber Seurity Training & Awareness\",\"description\":\"The FO shall ensure that personnel acknowledgment of security policies are formally documented for initial access authorization and renewed in each 24 month period or after major security policy changes.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":392}', '{\"new\": {\"id\": 392, \"title\": \"Cyber Seurity Training & Awareness\", \"number\": \"5.3-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall ensure that personnel acknowledgment of security policies are formally documented for initial access authorization and renewed in each 24 month period or after major security policy changes.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(399, 6, 'App\\Models\\Clause', 393, '', 'clauses', 'CREATED', '{\"number\":\"5.4\",\"title\":\"Network Security Boundaries, External Connections & Remote Access\",\"description\":\"Network design and layout constraints for security systems are defined in SEC-08\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":393}', '{\"new\": {\"id\": 393, \"title\": \"Network Security Boundaries, External Connections & Remote Access\", \"number\": \"5.4\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Network design and layout constraints for security systems are defined in SEC-08\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(400, 6, 'App\\Models\\Clause', 394, '', 'clauses', 'CREATED', '{\"number\":\"5.4.1-Para 1\",\"title\":\"Network Security Boundaries\",\"description\":\"The FO shall establish, define and document one or more Network Security Boundaries to protect IA&CS assets that are connected to a network.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":394}', '{\"new\": {\"id\": 394, \"title\": \"Network Security Boundaries\", \"number\": \"5.4.1-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall establish, define and document one or more Network Security Boundaries to protect IA&CS assets that are connected to a network.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(401, 6, 'App\\Models\\Clause', 395, '', 'clauses', 'CREATED', '{\"number\":\"5.4.1-Para 2\",\"title\":\"Network Security Boundaries\",\"description\":\"The Network Security Boundary shall segregate IA&CS assets from external network connections (i.e., Business\\/Enterprise, third parties, the Internet, etc.) by implementing and identifying one or access control point that restricts the flow of network traffic between the Network Security Boundary and external network connections.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":395}', '{\"new\": {\"id\": 395, \"title\": \"Network Security Boundaries\", \"number\": \"5.4.1-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The Network Security Boundary shall segregate IA&CS assets from external network connections (i.e., Business/Enterprise, third parties, the Internet, etc.) by implementing and identifying one or access control point that restricts the flow of network traffic between the Network Security Boundary and external network connections.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(402, 6, 'App\\Models\\Clause', 396, '', 'clauses', 'CREATED', '{\"number\":\"5.4.1-Para 3\",\"title\":\"Network Security Boundaries\",\"description\":\"At a minimum, these access control points shall limit inbound and outbound network traffic to only required logical protocols, ports and services with other traffic denied by default.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":396}', '{\"new\": {\"id\": 396, \"title\": \"Network Security Boundaries\", \"number\": \"5.4.1-Para 3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"At a minimum, these access control points shall limit inbound and outbound network traffic to only required logical protocols, ports and services with other traffic denied by default.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(403, 6, 'App\\Models\\Clause', 397, '', 'clauses', 'CREATED', '{\"number\":\"5.4.2\",\"title\":\"Information Sharing with External Connections\",\"description\":\"Where critical IA&CS Assets reside within a Network Security Boundary, the FO shall implement an intermediate security zone, such as, but not limited to, a DMZ to secure traffic to external systems.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":397}', '{\"new\": {\"id\": 397, \"title\": \"Information Sharing with External Connections\", \"number\": \"5.4.2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Where critical IA&CS Assets reside within a Network Security Boundary, the FO shall implement an intermediate security zone, such as, but not limited to, a DMZ to secure traffic to external systems.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(404, 6, 'App\\Models\\Clause', 398, '', 'clauses', 'CREATED', '{\"number\":\"5.4.3\",\"title\":\"Network Security Boundary Monitoring\",\"description\":\"The FO shall implement security event logging and monitoring at all access control points between the Network Security Boundary and external connections.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":398}', '{\"new\": {\"id\": 398, \"title\": \"Network Security Boundary Monitoring\", \"number\": \"5.4.3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall implement security event logging and monitoring at all access control points between the Network Security Boundary and external connections.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(405, 6, 'App\\Models\\Clause', 399, '', 'clauses', 'CREATED', '{\"number\":\"5.4.4\",\"title\":\"Remote access to Network Security Boundaries\",\"description\":\"The FO shall ensure that remote access, where implemented, to the network security boundaries shall use a documented and secure procedure that incorporates the following attributes:\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":399}', '{\"new\": {\"id\": 399, \"title\": \"Remote access to Network Security Boundaries\", \"number\": \"5.4.4\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall ensure that remote access, where implemented, to the network security boundaries shall use a documented and secure procedure that incorporates the following attributes:\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55');
