(409, 6, 'App\\Models\\Clause', 403, '', 'clauses', 'CREATED', '{\"number\":\"5.4.5\",\"title\":\"Security Controls for Public Networks\",\"description\":\"In cases where sensitive data related to the facility or IA&CS Assets traverses public networks, the FO shall ensure that such data is protected with strong encryption whilst in transit. FO shall conduct a risk analysis to determine the optimal protection strategy.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":403}', '{\"new\": {\"id\": 403, \"title\": \"Security Controls for Public Networks\", \"number\": \"5.4.5\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"In cases where sensitive data related to the facility or IA&CS Assets traverses public networks, the FO shall ensure that such data is protected with strong encryption whilst in transit. FO shall conduct a risk analysis to determine the optimal protection strategy.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(410, 6, 'App\\Models\\Clause', 404, '', 'clauses', 'CREATED', '{\"number\":\"5.5\",\"title\":\"Access &  System Security Management\",\"description\":\"\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":404}', '{\"new\": {\"id\": 404, \"title\": \"Access &  System Security Management\", \"number\": \"5.5\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(411, 6, 'App\\Models\\Clause', 405, '', 'clauses', 'CREATED', '{\"number\":\"5.5.1-Para 1\",\"title\":\"Access Management\",\"description\":\"The FO shall document and implement a process to authorize, based on need, physical and electronic access to IA&CS Assets and security systems. This access shall be reviewed every 12 months.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":405}', '{\"new\": {\"id\": 405, \"title\": \"Access Management\", \"number\": \"5.5.1-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall document and implement a process to authorize, based on need, physical and electronic access to IA&CS Assets and security systems. This access shall be reviewed every 12 months.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(412, 6, 'App\\Models\\Clause', 406, '', 'clauses', 'CREATED', '{\"number\":\"5.5.1-Para 2\",\"title\":\"Access Management\",\"description\":\"Access shall be immediately revoked when no longer needed.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":406}', '{\"new\": {\"id\": 406, \"title\": \"Access Management\", \"number\": \"5.5.1-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Access shall be immediately revoked when no longer needed.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(413, 6, 'App\\Models\\Clause', 407, '', 'clauses', 'CREATED', '{\"number\":\"5.5.1-Para 3\",\"title\":\"Access Management\",\"description\":\"Where shared accounts are required, due to the IA&CS constraints or operational considerations, for access to IA&CS assets, physical technical or procedural safeguards shall be implemented by the FO.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":407}', '{\"new\": {\"id\": 407, \"title\": \"Access Management\", \"number\": \"5.5.1-Para 3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Where shared accounts are required, due to the IA&CS constraints or operational considerations, for access to IA&CS assets, physical technical or procedural safeguards shall be implemented by the FO.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(414, 6, 'App\\Models\\Clause', 408, '', 'clauses', 'CREATED', '{\"number\":\"5.5.1-Para 4\",\"title\":\"Access Management\",\"description\":\"Subject to the IA&CS Asset capabilities and vendor support, the FO shall implement technical methods to enforce a limit to the number of successive unsuccessful authentication attempts.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":408}', '{\"new\": {\"id\": 408, \"title\": \"Access Management\", \"number\": \"5.5.1-Para 4\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Subject to the IA&CS Asset capabilities and vendor support, the FO shall implement technical methods to enforce a limit to the number of successive unsuccessful authentication attempts.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(415, 6, 'App\\Models\\Clause', 409, '', 'clauses', 'CREATED', '{\"number\":\"5.5.2-Para 1\",\"title\":\"Default Accounts\",\"description\":\"Subject to the IA&CS Asset capabilities and vendor support, the FO shall disable unnecessary default accounts on IA&CS Assets and all security systems including default operating system level accounts.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":409}', '{\"new\": {\"id\": 409, \"title\": \"Default Accounts\", \"number\": \"5.5.2-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Subject to the IA&CS Asset capabilities and vendor support, the FO shall disable unnecessary default accounts on IA&CS Assets and all security systems including default operating system level accounts.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(416, 6, 'App\\Models\\Clause', 410, '', 'clauses', 'CREATED', '{\"number\":\"5.5.2-Para 2\",\"title\":\"Default Accounts\",\"description\":\"Where default accounts are necessary, the FO shall change default passwords, subject to the IA&CS Asset and security system capabilities and vendor support.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":410}', '{\"new\": {\"id\": 410, \"title\": \"Default Accounts\", \"number\": \"5.5.2-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Where default accounts are necessary, the FO shall change default passwords, subject to the IA&CS Asset and security system capabilities and vendor support.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(417, 6, 'App\\Models\\Clause', 411, '', 'clauses', 'CREATED', '{\"number\":\"5.5.3\",\"title\":\"Passwords\",\"description\":\"Where password authentication for access to security systems and IA&CS is in use, the FO shall enforce, either through technical or procedural methods, the following passwords:\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":411}', '{\"new\": {\"id\": 411, \"title\": \"Passwords\", \"number\": \"5.5.3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Where password authentication for access to security systems and IA&CS is in use, the FO shall enforce, either through technical or procedural methods, the following passwords:\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(418, 6, 'App\\Models\\Clause', 412, '', 'clauses', 'CREATED', '{\"number\":\"5.5.3.1\",\"title\":\"Passwords\",\"description\":\"A minimum password length that is at least twelve characters or the maximum length support by the IA&CS asset if less than 12 characters.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":412}', '{\"new\": {\"id\": 412, \"title\": \"Passwords\", \"number\": \"5.5.3.1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"A minimum password length that is at least twelve characters or the maximum length support by the IA&CS asset if less than 12 characters.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(419, 6, 'App\\Models\\Clause', 413, '', 'clauses', 'CREATED', '{\"number\":\"5.5.3.2\",\"title\":\"Passwords\",\"description\":\"A minimum password complexity that includes three or more characters types: uppercase alphabetic, lowercase alphabetic, numeric, non-alphanumeric, or the maximum complexity supported by the IA&CS Asset.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":413}', '{\"new\": {\"id\": 413, \"title\": \"Passwords\", \"number\": \"5.5.3.2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"A minimum password complexity that includes three or more characters types: uppercase alphabetic, lowercase alphabetic, numeric, non-alphanumeric, or the maximum complexity supported by the IA&CS Asset.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(420, 6, 'App\\Models\\Clause', 414, '', 'clauses', 'CREATED', '{\"number\":\"5.5.3.3\",\"title\":\"Passwords\",\"description\":\"Passwords shall automatically expire every 6 months maximum.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":414}', '{\"new\": {\"id\": 414, \"title\": \"Passwords\", \"number\": \"5.5.3.3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Passwords shall automatically expire every 6 months maximum.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(421, 6, 'App\\Models\\Clause', 415, '', 'clauses', 'CREATED', '{\"number\":\"5.5.4-Para 1\",\"title\":\"Threat Protection and Prevention\",\"description\":\"Subject to IA&CS Asset and security system capabilities and vendor support, all IA&CS Asset and security systems shall have the latest malware protection and prevention software installed, active and maintained.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":415}', '{\"new\": {\"id\": 415, \"title\": \"Threat Protection and Prevention\", \"number\": \"5.5.4-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Subject to IA&CS Asset and security system capabilities and vendor support, all IA&CS Asset and security systems shall have the latest malware protection and prevention software installed, active and maintained.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(422, 6, 'App\\Models\\Clause', 416, '', 'clauses', 'CREATED', '{\"number\":\"5.5.4-Para 2\",\"title\":\"Threat Protection and Prevention\",\"description\":\"The FO shall implement methods to proactively detect and counter emerging threats.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":416}', '{\"new\": {\"id\": 416, \"title\": \"Threat Protection and Prevention\", \"number\": \"5.5.4-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall implement methods to proactively detect and counter emerging threats.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(423, 6, 'App\\Models\\Clause', 417, '', 'clauses', 'CREATED', '{\"number\":\"5.5.5\",\"title\":\"Security Event Logging and Alerting\",\"description\":\"The FO shall, subject to IA&CS Asset capabilities and vendor support, enable continuous security event logging on the all IA&CS Assets. This data shall be stored in a central location as well as local site.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":417}', '{\"new\": {\"id\": 417, \"title\": \"Security Event Logging and Alerting\", \"number\": \"5.5.5\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall, subject to IA&CS Asset capabilities and vendor support, enable continuous security event logging on the all IA&CS Assets. This data shall be stored in a central location as well as local site.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(424, 6, 'App\\Models\\Clause', 418, '', 'clauses', 'CREATED', '{\"number\":\"5.5.6-Para 1\",\"title\":\"Security Patch Management\",\"description\":\"The FO shall document and implement a process to manage security patches for security systems and IA&CS Assets.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":418}', '{\"new\": {\"id\": 418, \"title\": \"Security Patch Management\", \"number\": \"5.5.6-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall document and implement a process to manage security patches for security systems and IA&CS Assets.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(425, 6, 'App\\Models\\Clause', 419, '', 'clauses', 'CREATED', '{\"number\":\"5.5.6-Para 2\",\"title\":\"Security Patch Management\",\"description\":\"Subject to IA&CS Asset capabilities and vendor support, the FO shall install the most recent security patches for IA&CS Asset and security system operating systems, firmware and software applications.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":419}', '{\"new\": {\"id\": 419, \"title\": \"Security Patch Management\", \"number\": \"5.5.6-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Subject to IA&CS Asset capabilities and vendor support, the FO shall install the most recent security patches for IA&CS Asset and security system operating systems, firmware and software applications.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(426, 6, 'App\\Models\\Clause', 420, '', 'clauses', 'CREATED', '{\"number\":\"5.5.6-Para 3\",\"title\":\"Security Patch Management\",\"description\":\"Security patches shalll be installed within 4 weeks of becoming available from the vendor.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":420}', '{\"new\": {\"id\": 420, \"title\": \"Security Patch Management\", \"number\": \"5.5.6-Para 3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Security patches shalll be installed within 4 weeks of becoming available from the vendor.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(427, 6, 'App\\Models\\Clause', 421, '', 'clauses', 'CREATED', '{\"number\":\"5.5.7\",\"title\":\"IA&CS Asset Hardening Procedures\",\"description\":\"The FO shall document and implement security hardening procedures for all IA&CS Assets and security systems, based on industry standards and vendor guidelines, subject to IA&CS Asset and security system capabilities and vendor support.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":421}', '{\"new\": {\"id\": 421, \"title\": \"IA&CS Asset Hardening Procedures\", \"number\": \"5.5.7\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall document and implement security hardening procedures for all IA&CS Assets and security systems, based on industry standards and vendor guidelines, subject to IA&CS Asset and security system capabilities and vendor support.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(428, 6, 'App\\Models\\Clause', 422, '', 'clauses', 'CREATED', '{\"number\":\"5.5.8-Para 1\",\"title\":\"Disable Unnecessary Services\",\"description\":\"The FO shall disable all unnecessary features, services and associated logical network protocols and ports (e.g. scripts, applications, network services, etc.) on all IA&CS Assets and security systems, subject to the IA&CS Asset capabilities and vendor support.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":422}', '{\"new\": {\"id\": 422, \"title\": \"Disable Unnecessary Services\", \"number\": \"5.5.8-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall disable all unnecessary features, services and associated logical network protocols and ports (e.g. scripts, applications, network services, etc.) on all IA&CS Assets and security systems, subject to the IA&CS Asset capabilities and vendor support.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(429, 6, 'App\\Models\\Clause', 423, '', 'clauses', 'CREATED', '{\"number\":\"5.5.8-Para 2\",\"title\":\"Disable Unnecessary Services\",\"description\":\"All security system and IA&CS workstations shall have the USB Ports, CD Readers and other ports to connect external devices disabled. Users shall not be permitted to install any additional software or updates.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":423}', '{\"new\": {\"id\": 423, \"title\": \"Disable Unnecessary Services\", \"number\": \"5.5.8-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"All security system and IA&CS workstations shall have the USB Ports, CD Readers and other ports to connect external devices disabled. Users shall not be permitted to install any additional software or updates.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(430, 6, 'App\\Models\\Clause', 424, '', 'clauses', 'CREATED', '{\"number\":\"5.5.8-Para 3\",\"title\":\"Disable Unnecessary Services\",\"description\":\"Email services shall block any attachment that has macros or scripts.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":424}', '{\"new\": {\"id\": 424, \"title\": \"Disable Unnecessary Services\", \"number\": \"5.5.8-Para 3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Email services shall block any attachment that has macros or scripts.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(431, 6, 'App\\Models\\Clause', 425, '', 'clauses', 'CREATED', '{\"number\":\"5.5.9-Para 1\",\"title\":\"Asset Management\",\"description\":\"The FO shall document and review its inventory of IA&CS Asset to ensure completeness and correctness, at least once every 12 calendar months.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":425}', '{\"new\": {\"id\": 425, \"title\": \"Asset Management\", \"number\": \"5.5.9-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall document and review its inventory of IA&CS Asset to ensure completeness and correctness, at least once every 12 calendar months.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(432, 6, 'App\\Models\\Clause', 426, '', 'clauses', 'CREATED', '{\"number\":\"5.5.9-Para 2\",\"title\":\"Asset Management\",\"description\":\"The FO shall ensure that any discrepancies are resolved and\\/or reported.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":426}', '{\"new\": {\"id\": 426, \"title\": \"Asset Management\", \"number\": \"5.5.9-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall ensure that any discrepancies are resolved and/or reported.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(433, 6, 'App\\Models\\Clause', 427, '', 'clauses', 'CREATED', '{\"number\":\"5.6-Para 1\",\"title\":\"Cybersecurity Incident Response\",\"description\":\"The FO shall document and implement one or more Cybersecurity incident response plan(s) in accordance with industry standards, such as, ut not limited to, NIST SP 800-34, and FO contingency plans.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":427}', '{\"new\": {\"id\": 427, \"title\": \"Cybersecurity Incident Response\", \"number\": \"5.6-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall document and implement one or more Cybersecurity incident response plan(s) in accordance with industry standards, such as, ut not limited to, NIST SP 800-34, and FO contingency plans.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(434, 6, 'App\\Models\\Clause', 428, '', 'clauses', 'CREATED', '{\"number\":\"5.6-Para 2\",\"title\":\"Cybersecurity Incident Response\",\"description\":\"This plan shall be tested every 12 months. The results of the annual test shall be summarized and sent to HCIS.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":428}', '{\"new\": {\"id\": 428, \"title\": \"Cybersecurity Incident Response\", \"number\": \"5.6-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"This plan shall be tested every 12 months. The results of the annual test shall be summarized and sent to HCIS.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(435, 6, 'App\\Models\\Clause', 429, '', 'clauses', 'CREATED', '{\"number\":\"5.7-Para 1\",\"title\":\"Backup and Recovery\",\"description\":\"Back-up copies of data and software shall be taken and tested regularly in accordance with the company backup policy. Backups shall be encrypted and stored in a secured physically separate location.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":429}', '{\"new\": {\"id\": 429, \"title\": \"Backup and Recovery\", \"number\": \"5.7-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"Back-up copies of data and software shall be taken and tested regularly in accordance with the company backup policy. Backups shall be encrypted and stored in a secured physically separate location.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(436, 6, 'App\\Models\\Clause', 430, '', 'clauses', 'CREATED', '{\"number\":\"5.7-Para 2\",\"title\":\"Backup and Recovery\",\"description\":\"FO shall have a disaster recovery plan that is periodically tested.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":430}', '{\"new\": {\"id\": 430, \"title\": \"Backup and Recovery\", \"number\": \"5.7-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"FO shall have a disaster recovery plan that is periodically tested.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(437, 6, 'App\\Models\\Clause', 431, '', 'clauses', 'CREATED', '{\"number\":\"5.8\",\"title\":\"Configuration Change Management\",\"description\":\"All Software and hardware changes to security systems and IA&CS assets shall be documented and managed through a change management process. Significant changes shall be tested to ensure they do not compromise IA&CS security controls.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":431}', '{\"new\": {\"id\": 431, \"title\": \"Configuration Change Management\", \"number\": \"5.8\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"All Software and hardware changes to security systems and IA&CS assets shall be documented and managed through a change management process. Significant changes shall be tested to ensure they do not compromise IA&CS security controls.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(438, 6, 'App\\Models\\Clause', 432, '', 'clauses', 'CREATED', '{\"number\":\"5.9\",\"title\":\"Data Protection Program\",\"description\":\"The FO shall ensure that its Data Protection Program clearly assigns ownership and the responsibility, along with classification and protection of all applicable information regarding IA&CS and security systems.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":432}', '{\"new\": {\"id\": 432, \"title\": \"Data Protection Program\", \"number\": \"5.9\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall ensure that its Data Protection Program clearly assigns ownership and the responsibility, along with classification and protection of all applicable information regarding IA&CS and security systems.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(439, 6, 'App\\Models\\Clause', 433, '', 'clauses', 'CREATED', '{\"number\":\"5.10-Para 1\",\"title\":\"Media Disposal & Sanitization\",\"description\":\"The FO shall implement and document operational procedures to ensure that electronic storage media associated with IA&CS assets or security systems are properly sanitized or destroyed prior to disposal or reuse outside of the IA&CS.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":433}', '{\"new\": {\"id\": 433, \"title\": \"Media Disposal & Sanitization\", \"number\": \"5.10-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"The FO shall implement and document operational procedures to ensure that electronic storage media associated with IA&CS assets or security systems are properly sanitized or destroyed prior to disposal or reuse outside of the IA&CS.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(440, 6, 'App\\Models\\Clause', 434, '', 'clauses', 'CREATED', '{\"number\":\"5.10-Para 2\",\"title\":\"Media Disposal & Sanitization\",\"description\":\"FO shall refer to the related guidelines such as NIST SP 800-88 regarding media disposal and sanitization.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":434}', '{\"new\": {\"id\": 434, \"title\": \"Media Disposal & Sanitization\", \"number\": \"5.10-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"FO shall refer to the related guidelines such as NIST SP 800-88 regarding media disposal and sanitization.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(441, 6, 'App\\Models\\Clause', 435, '', 'clauses', 'CREATED', '{\"number\":\"5.11\",\"title\":\"Physical Protection of IA&CS\",\"description\":\"While this directive generally addresses the online access to IA&CS assets, the FO is also responsible for securing the physical asset as well. IA&CS assets shall be installed in secured areas or cabinets that limit access to the devices.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":435}', '{\"new\": {\"id\": 435, \"title\": \"Physical Protection of IA&CS\", \"number\": \"5.11\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"While this directive generally addresses the online access to IA&CS assets, the FO is also responsible for securing the physical asset as well. IA&CS assets shall be installed in secured areas or cabinets that limit access to the devices.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(442, 6, 'App\\Models\\Clause', 436, '', 'clauses', 'CREATED', '{\"number\":\"7-Para 1\",\"title\":\"Proof of Compliance\",\"description\":\"FO shall provide HCIS with Proof fo Compliance (PoC), as part of the Stage 3 workflow, to explain and demonstrate how the FO is complying withi specific requirements in this directive. This will augment the Stage 3 submission which covers all items. The Stage 3 submission, content and format are specified in SEC-12 section 6.3.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":436}', '{\"new\": {\"id\": 436, \"title\": \"Proof of Compliance\", \"number\": \"7-Para 1\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"FO shall provide HCIS with Proof fo Compliance (PoC), as part of the Stage 3 workflow, to explain and demonstrate how the FO is complying withi specific requirements in this directive. This will augment the Stage 3 submission which covers all items. The Stage 3 submission, content and format are specified in SEC-12 section 6.3.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(443, 6, 'App\\Models\\Clause', 437, '', 'clauses', 'CREATED', '{\"number\":\"7-Para 2\",\"title\":\"Proof of Compliance\",\"description\":\"This PoC shall be submitted when available but prior to the project commissioning.\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":437}', '{\"new\": {\"id\": 437, \"title\": \"Proof of Compliance\", \"number\": \"7-Para 2\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"This PoC shall be submitted when available but prior to the project commissioning.\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(444, 6, 'App\\Models\\Clause', 438, '', 'clauses', 'CREATED', '{\"number\":\"7-Para 3\",\"title\":\"Proof of Compliance\",\"description\":\"This PoC shall provide details for each of the requirements listed below\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":438}', '{\"new\": {\"id\": 438, \"title\": \"Proof of Compliance\", \"number\": \"7-Para 3\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"This PoC shall provide details for each of the requirements listed below\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
(445, 6, 'App\\Models\\Clause', 439, '', 'clauses', 'CREATED', '{\"number\":\"7-Para 4\",\"title\":\"Proof of Compliance\",\"description\":\"In all cases the responses shall be specific in nature and include adequate technical details to demonstrate compliance to HCIS\",\"standard_id\":5,\"updated_at\":\"2021-11-26T10:45:55.000000Z\",\"created_at\":\"2021-11-26T10:45:55.000000Z\",\"id\":439}', '{\"new\": {\"id\": 439, \"title\": \"Proof of Compliance\", \"number\": \"7-Para 4\", \"created_at\": \"2021-11-26 10:45:55\", \"updated_at\": \"2021-11-26 10:45:55\", \"description\": \"In all cases the responses shall be specific in nature and include adequate technical details to demonstrate compliance to HCIS\", \"standard_id\": 5}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 10:45:55', '2021-11-26 10:45:55'),
