(653, 4, 'App\\Models\\Clause', 592, '', 'clauses', 'CREATED', '{\"number\":\"1-4\",\"title\":\"Periodical Cybersecurity Review and Audit\",\"description\":\"To ensure that cybersecurity controls are implemented and in compliance with organizational policies and procedures, as well as related national and international laws, regulations and agreements.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":592}', '{\"new\": {\"id\": 592, \"title\": \"Periodical Cybersecurity Review and Audit\", \"number\": \"1-4\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"To ensure that cybersecurity controls are implemented and in compliance with organizational policies and procedures, as well as related national and international laws, regulations and agreements.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(654, 4, 'App\\Models\\Clause', 593, '', 'clauses', 'CREATED', '{\"number\":\"1-4-1\",\"title\":\"Periodical Cybersecurity Review and Audit\",\"description\":\"With reference to the ECC control 1-8-1, the organization\'s cybersecurity function must review the implementation of CSCC at least once annually.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":593}', '{\"new\": {\"id\": 593, \"title\": \"Periodical Cybersecurity Review and Audit\", \"number\": \"1-4-1\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"With reference to the ECC control 1-8-1, the organization\'s cybersecurity function must review the implementation of CSCC at least once annually.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
