(673, 4, 'App\\Models\\Clause', 612, '', 'clauses', 'CREATED', '{\"number\":\"2-2-1-8\",\"title\":\"Identity and Access Management\",\"description\":\"Prohibiting direct access and interaction with database for all users except for database administrators. Users\' access and interaction with databases must be through application only, with consideration given to applying security solutions that limits or prohibit visibility of classified data to database administrators.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":612}', '{\"new\": {\"id\": 612, \"title\": \"Identity and Access Management\", \"number\": \"2-2-1-8\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Prohibiting direct access and interaction with database for all users except for database administrators. Users\' access and interaction with databases must be through application only, with consideration given to applying security solutions that limits or prohibit visibility of classified data to database administrators.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(674, 4, 'App\\Models\\Clause', 613, '', 'clauses', 'CREATED', '{\"number\":\"2-2-2\",\"title\":\"Identity and Access Management\",\"description\":\"With reference to ECC subcontrol 2-2-3-5, user identities and access rights to critical systems must be reviewed at least once every three months.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":613}', '{\"new\": {\"id\": 613, \"title\": \"Identity and Access Management\", \"number\": \"2-2-2\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"With reference to ECC subcontrol 2-2-3-5, user identities and access rights to critical systems must be reviewed at least once every three months.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
