(693, 4, 'App\\Models\\Clause', 632, '', 'clauses', 'CREATED', '{\"number\":\"2-4-1-7\",\"title\":\"Network Security Management\",\"description\":\"Critical systems that provide services to a limited number of organizations (not individuals), shall use networks isolated from the Internet.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":632}', '{\"new\": {\"id\": 632, \"title\": \"Network Security Management\", \"number\": \"2-4-1-7\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Critical systems that provide services to a limited number of organizations (not individuals), shall use networks isolated from the Internet.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(694, 4, 'App\\Models\\Clause', 633, '', 'clauses', 'CREATED', '{\"number\":\"2-4-1-8\",\"title\":\"Network Security Management\",\"description\":\"Protecting against Distributed Denial of Service (DDoS) attacks to limit risks arising from these attacks.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":633}', '{\"new\": {\"id\": 633, \"title\": \"Network Security Management\", \"number\": \"2-4-1-8\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Protecting against Distributed Denial of Service (DDoS) attacks to limit risks arising from these attacks.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(695, 4, 'App\\Models\\Clause', 634, '', 'clauses', 'CREATED', '{\"number\":\"2-4-1-9\",\"title\":\"Network Security Management\",\"description\":\"Allowing only whitelisting for critical systems\' firewall access lists.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":634}', '{\"new\": {\"id\": 634, \"title\": \"Network Security Management\", \"number\": \"2-4-1-9\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Allowing only whitelisting for critical systems\' firewall access lists.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(696, 4, 'App\\Models\\Clause', 635, '', 'clauses', 'CREATED', '{\"number\":\"2-5\",\"title\":\"Mobile Device Security\",\"description\":\"To ensure the protection of mobile devices (including laptops, smartphones, tablets) from cyber risks and to ensure the secure handling of the organization\'s information (including sensitive information) while utilizing Bring Your Own Device (B YOD) policy.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":635}', '{\"new\": {\"id\": 635, \"title\": \"Mobile Device Security\", \"number\": \"2-5\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"To ensure the protection of mobile devices (including laptops, smartphones, tablets) from cyber risks and to ensure the secure handling of the organization\'s information (including sensitive information) while utilizing Bring Your Own Device (B YOD) policy.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(697, 4, 'App\\Models\\Clause', 636, '', 'clauses', 'CREATED', '{\"number\":\"2-5-1\",\"title\":\"Mobile Device Security\",\"description\":\"In addition to the subcontrols in ECC control 2-6-3, cybersecurity requirements for mobile devices security and BYOD in the organization must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":636}', '{\"new\": {\"id\": 636, \"title\": \"Mobile Device Security\", \"number\": \"2-5-1\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"In addition to the subcontrols in ECC control 2-6-3, cybersecurity requirements for mobile devices security and BYOD in the organization must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(698, 4, 'App\\Models\\Clause', 637, '', 'clauses', 'CREATED', '{\"number\":\"2-5-1-1\",\"title\":\"Mobile Device Security\",\"description\":\"Prohibiting access to critical systems from mobile devices except for a temporary period only, after assessing the risks and obtaining the necessary approvals from the cybersecurity function in the organization.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":637}', '{\"new\": {\"id\": 637, \"title\": \"Mobile Device Security\", \"number\": \"2-5-1-1\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Prohibiting access to critical systems from mobile devices except for a temporary period only, after assessing the risks and obtaining the necessary approvals from the cybersecurity function in the organization.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(699, 4, 'App\\Models\\Clause', 638, '', 'clauses', 'CREATED', '{\"number\":\"2-5-1-2\",\"title\":\"Mobile Device Security\",\"description\":\"Implementing full disk encryption for mobile devices with access to critical systems.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":638}', '{\"new\": {\"id\": 638, \"title\": \"Mobile Device Security\", \"number\": \"2-5-1-2\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Implementing full disk encryption for mobile devices with access to critical systems.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(700, 4, 'App\\Models\\Clause', 639, '', 'clauses', 'CREATED', '{\"number\":\"2-6\",\"title\":\"Data and Information Protection\",\"description\":\"To ensure the confidentiality, integrity and availability of the organization\'s data and information as per organizational policies and procedures, and related laws and regulations.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":639}', '{\"new\": {\"id\": 639, \"title\": \"Data and Information Protection\", \"number\": \"2-6\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"To ensure the confidentiality, integrity and availability of the organization\'s data and information as per organizational policies and procedures, and related laws and regulations.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(701, 4, 'App\\Models\\Clause', 640, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1\",\"title\":\"Data and Information Protection\",\"description\":\"In additional to the subcontrols in ECC control 2-7-3, cybersecurity requirements for protecting and handling data and information must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":640}', '{\"new\": {\"id\": 640, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"In additional to the subcontrols in ECC control 2-7-3, cybersecurity requirements for protecting and handling data and information must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(702, 4, 'App\\Models\\Clause', 641, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-1\",\"title\":\"Data and Information Protection\",\"description\":\"Prohibiting the use of critical system\'s data in any environment other than production environment, except after applying strict controls for protecting that data, such as: data masking or data scrambling techniques.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":641}', '{\"new\": {\"id\": 641, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-1\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Prohibiting the use of critical system\'s data in any environment other than production environment, except after applying strict controls for protecting that data, such as: data masking or data scrambling techniques.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(703, 4, 'App\\Models\\Clause', 642, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-2\",\"title\":\"Data and Information Protection\",\"description\":\"Classifying all data within critical systems.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":642}', '{\"new\": {\"id\": 642, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-2\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Classifying all data within critical systems.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(704, 4, 'App\\Models\\Clause', 643, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-3\",\"title\":\"Data and Information Protection\",\"description\":\"Protecting classified data of critical systems using data leakage prevention techniques.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":643}', '{\"new\": {\"id\": 643, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-3\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Protecting classified data of critical systems using data leakage prevention techniques.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(705, 4, 'App\\Models\\Clause', 644, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-4\",\"title\":\"Data and Information Protection\",\"description\":\"Identifying retention period for critical systems-associated data, in accordance with relevant legislations. Only required data must be retained in critical systems\' production environments.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":644}', '{\"new\": {\"id\": 644, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-4\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Identifying retention period for critical systems-associated data, in accordance with relevant legislations. Only required data must be retained in critical systems\' production environments.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(706, 4, 'App\\Models\\Clause', 645, '', 'clauses', 'CREATED', '{\"number\":\"2-6-1-5\",\"title\":\"Data and Information Protection\",\"description\":\"Prohibiting the transfer of any critical systems\' data from production environment to any other environment.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":645}', '{\"new\": {\"id\": 645, \"title\": \"Data and Information Protection\", \"number\": \"2-6-1-5\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Prohibiting the transfer of any critical systems\' data from production environment to any other environment.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(707, 4, 'App\\Models\\Clause', 646, '', 'clauses', 'CREATED', '{\"number\":\"2-7\",\"title\":\"Cryptography\",\"description\":\"To ensure the proper and efficient use of cryptograpy to protect information assets as per organizational policies and procedures, and related laws and regulations.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":646}', '{\"new\": {\"id\": 646, \"title\": \"Cryptography\", \"number\": \"2-7\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"To ensure the proper and efficient use of cryptograpy to protect information assets as per organizational policies and procedures, and related laws and regulations.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(708, 4, 'App\\Models\\Clause', 647, '', 'clauses', 'CREATED', '{\"number\":\"2-7-1\",\"title\":\"Cryptography\",\"description\":\"In additional to the subcontrols in ECC control 2-8-3, cybersecurity requirements for cryptography must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":647}', '{\"new\": {\"id\": 647, \"title\": \"Cryptography\", \"number\": \"2-7-1\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"In additional to the subcontrols in ECC control 2-8-3, cybersecurity requirements for cryptography must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
