(710, 4, 'App\\Models\\Clause', 649, '', 'clauses', 'CREATED', '{\"number\":\"2-7-1-2\",\"title\":\"Cryptography\",\"description\":\"Encrypting all critical systems\' data-at-rest at the level fo files, database or certain columns within database.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":649}', '{\"new\": {\"id\": 649, \"title\": \"Cryptography\", \"number\": \"2-7-1-2\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Encrypting all critical systems\' data-at-rest at the level fo files, database or certain columns within database.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(711, 4, 'App\\Models\\Clause', 650, '', 'clauses', 'CREATED', '{\"number\":\"2-7-1-3\",\"title\":\"Cryptography\",\"description\":\"Using secure and up-to-date methods, algorithms, keys and devices in accordance with what NCA issues in this regard.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":650}', '{\"new\": {\"id\": 650, \"title\": \"Cryptography\", \"number\": \"2-7-1-3\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Using secure and up-to-date methods, algorithms, keys and devices in accordance with what NCA issues in this regard.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(712, 4, 'App\\Models\\Clause', 651, '', 'clauses', 'CREATED', '{\"number\":\"2-8\",\"title\":\"Backup and Recovery Management\",\"description\":\"To ensure the protection of the organization\'s data and information, including information systems\' and software configuration from cyber risks as per organizational policies and procedures, and related laws and regulations.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":651}', '{\"new\": {\"id\": 651, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"To ensure the protection of the organization\'s data and information, including information systems\' and software configuration from cyber risks as per organizational policies and procedures, and related laws and regulations.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(713, 4, 'App\\Models\\Clause', 652, '', 'clauses', 'CREATED', '{\"number\":\"2-8-1\",\"title\":\"Backup and Recovery Management\",\"description\":\"In addition to the subcontrols in ECC control 2-9-3, cybersecurity requirements for backup and recovery management must include at least the following:\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":652}', '{\"new\": {\"id\": 652, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8-1\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"In addition to the subcontrols in ECC control 2-9-3, cybersecurity requirements for backup and recovery management must include at least the following:\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(714, 4, 'App\\Models\\Clause', 653, '', 'clauses', 'CREATED', '{\"number\":\"2-8-1-1\",\"title\":\"Backup and Recovery Management\",\"description\":\"Scope and coverage of online and offline backups shall cover all critical systems.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":653}', '{\"new\": {\"id\": 653, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8-1-1\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Scope and coverage of online and offline backups shall cover all critical systems.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(715, 4, 'App\\Models\\Clause', 654, '', 'clauses', 'CREATED', '{\"number\":\"2-8-1-2\",\"title\":\"Backup and Recovery Management\",\"description\":\"Performing backup within planned intervals, according to the organization\'s risk assessment. NCA recommends performing bacup for critical systems on a daily basis.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":654}', '{\"new\": {\"id\": 654, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8-1-2\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Performing backup within planned intervals, according to the organization\'s risk assessment. NCA recommends performing bacup for critical systems on a daily basis.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
(716, 4, 'App\\Models\\Clause', 655, '', 'clauses', 'CREATED', '{\"number\":\"2-8-1-3\",\"title\":\"Backup and Recovery Management\",\"description\":\"Securing access, storage and transfer of critical systems\' backups and storage media, and protecting it from destruction, unauthorized access or modification.\",\"standard_id\":4,\"updated_at\":\"2021-11-26T12:45:03.000000Z\",\"created_at\":\"2021-11-26T12:45:03.000000Z\",\"id\":655}', '{\"new\": {\"id\": 655, \"title\": \"Backup and Recovery Management\", \"number\": \"2-8-1-3\", \"created_at\": \"2021-11-26 12:45:03\", \"updated_at\": \"2021-11-26 12:45:03\", \"description\": \"Securing access, storage and transfer of critical systems\' backups and storage media, and protecting it from destruction, unauthorized access or modification.\", \"standard_id\": 4}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 12:45:03', '2021-11-26 12:45:03'),
