(1144, 4, 'App\\Models\\Clause', 1083, '', 'clauses', 'CREATED', '{\"number\":\"1-5-4\",\"title\":\"Cybersecurity Risk Management\",\"description\":\"The cybersecurity risk management methodology and procedures must be reviewed periodically according to planned intervals or upon changes to related laws and regulations. Changes and reviews must be approved and documented.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T13:46:24.000000Z\",\"created_at\":\"2021-11-26T13:46:24.000000Z\",\"id\":1083}', '{\"new\": {\"id\": 1083, \"title\": \"Cybersecurity Risk Management\", \"number\": \"1-5-4\", \"created_at\": \"2021-11-26 13:46:24\", \"updated_at\": \"2021-11-26 13:46:24\", \"description\": \"The cybersecurity risk management methodology and procedures must be reviewed periodically according to planned intervals or upon changes to related laws and regulations. Changes and reviews must be approved and documented.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 13:46:24', '2021-11-26 13:46:24'),
(1145, 4, 'App\\Models\\Clause', 1084, '', 'clauses', 'CREATED', '{\"number\":\"1-6\",\"title\":\"Cybersecurity in Information and Technology Project Management\",\"description\":\"To ensure that cybersecurity requirements are included in project management methodology and procedures in order to protect the confidentiality, integrity and availability of information and technology assets as per organization policies and procedures, and related laws and regulations.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T13:46:24.000000Z\",\"created_at\":\"2021-11-26T13:46:24.000000Z\",\"id\":1084}', '{\"new\": {\"id\": 1084, \"title\": \"Cybersecurity in Information and Technology Project Management\", \"number\": \"1-6\", \"created_at\": \"2021-11-26 13:46:24\", \"updated_at\": \"2021-11-26 13:46:24\", \"description\": \"To ensure that cybersecurity requirements are included in project management methodology and procedures in order to protect the confidentiality, integrity and availability of information and technology assets as per organization policies and procedures, and related laws and regulations.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 13:46:24', '2021-11-26 13:46:24'),
(1146, 4, 'App\\Models\\Clause', 1085, '', 'clauses', 'CREATED', '{\"number\":\"1-6-1\",\"title\":\"Cybersecurity in Information and Technology Project Management\",\"description\":\"Cybersecurity requirements must be included in project and asset (information\\/ technology) change management methodology and procedures to identify and manage cybersecurity risks as part of project management lifecycle. The cybersecurity requirements must be a key part of the overall requirements of technology projects.\",\"standard_id\":3,\"updated_at\":\"2021-11-26T13:46:24.000000Z\",\"created_at\":\"2021-11-26T13:46:24.000000Z\",\"id\":1085}', '{\"new\": {\"id\": 1085, \"title\": \"Cybersecurity in Information and Technology Project Management\", \"number\": \"1-6-1\", \"created_at\": \"2021-11-26 13:46:24\", \"updated_at\": \"2021-11-26 13:46:24\", \"description\": \"Cybersecurity requirements must be included in project and asset (information/ technology) change management methodology and procedures to identify and manage cybersecurity risks as part of project management lifecycle. The cybersecurity requirements must be a key part of the overall requirements of technology projects.\", \"standard_id\": 3}, \"old\": null, \"changed\": null}', 0, 0, '2021-11-26 13:46:24', '2021-11-26 13:46:24'),
